#include <iostream> #include <vector> int main() { std::vector<int> vec; for (int i = 0; i < 10; ++i) { vec.push_back(i); } std::vector<int> vec2 = vec;  // create a copy for modification for (int i = 0; i < vec2.size(); ++i) { vec2[i] = i * 2; } for (int i = 0; i < vec.size(); ++i) { std::cout << vec[i] << " "; } std::cout << std::endl; for (int i = 0; i < vec2.size(); ++i) { std::cout << vec2[i] << " "; } std::cout << std::endl; return 0; }